/*
* areaData 是压缩后的，你可以到 http://tool.oschina.net/codeformat/json 去格式化数据以方便查看
*
* $each(Object,function(p1,p2){
*	this;	// 这里的this指向每次遍历的当前属性值
* 	p1;p2;	// 访问附加参数 p1为每次遍历的属性，p2为每次遍历的属性值
* },['参数1',' 参数2'])
*/

// 显示选择的省，市和县
var selectedArr = [];
// 触发省，市或县的元素
var _province = $("#area .container .show .province"),
	_city = $("#area .container .show .city"),
	_county = $("#area .container .show .county");

// 展示的区域的元素
var data = $("#area .container .data");

// 选中相关的县
var selectCounty = function(county){
	selectedArr.length == 3 ? selectedArr.splice(2,1,county) : selectedArr.push(county);
	// console.log(selectedArr);
	_county.text(selectedArr[2].split("|")[1]);
}

// 获取县
var initCounty = function(province,city){
	selectedArr.push(city);
	// console.log(selectedArr);
	_city.text(selectedArr[1].split("|")[1]);
	data.find('.lis li').remove();
	areaData[province][city].forEach(function(value,index){
		data.find('.lis').append(`<li onclick='selectCounty("${value}")'>${value.split("|")[1]}</li>`)
	});
}

// 获取市
var initCity = function(province){
	selectedArr.length > 1 ? selectedArr.splice(1,selectedArr.length-1) : selectedArr.push(province);
	// console.log(selectedArr);
	_province.text(selectedArr[0].split("|")[1]);
	data.find('.lis li').remove();
	$.each(areaData[province],function(city){
		data.find('.lis').append(`<li onclick='initCounty("${province}","${city}")'>${city.split('|')[1]}</li>`)
	});
}

init()
function init(){
	_province.click(function(){
		selectedArr = [];
		data.find('.hint').hide();
		_province.text('请选择省');
		_city.text('请选择市');
		_county.text('请选择县');
		data.find('.lis li').remove();
		$.each(areaData,function(province){
			data.find('.lis').append(`<li onclick='initCity("${province}")'>${province.split('|')[1]}</li>`);
		});
	});
	_city.click(function(){
		selectedArr.length >= 2 ? selectedArr.splice(1,selectedArr.length-1) : selectedArr; 
		// console.log(selectedArr);
		_city.text('请选择市');
		_county.text('请选择县');
		data.find('.lis li').remove();
		$.each(areaData[selectedArr[0]],function(city){
			data.find('.lis').append(`<li onclick='initCounty("${selectedArr[0]}","${city}")'>${city.split('|')[1]}</li>`)
		})
	});
	_county.click(function(){
		selectedArr.pop();
		// console.log(selectedArr);
		_county.text('请选择县')
	})
}




	
	

